import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../context/UserContext';
import './InvoiceManagement.css';

const InvoiceManagement = () => {
  const [invoices, setInvoices] = useState([]);
  const { user } = useContext(UserContext);
  const [newInvoice, setNewInvoice] = useState({
    invoiceNumber: '',
    date: '',
    time: '',
    receiptType: '',
    narrative: '',
    amount: '',
    currency: '',
    image: null
  });
  const [editInvoice, setEditInvoice] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMode, setPopupMode] = useState(''); // 'add' or 'edit'

  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/invoices');
        if (response.data && Array.isArray(response.data.invoices)) {
          setInvoices(response.data.invoices);
        } else {
          console.error('Unexpected response format', response.data);
          setInvoices([]);
        }
      } catch (error) {
        console.error('Failed to fetch invoices', error);
      }
    };
    fetchInvoices();
  }, []);

  const handleAddInvoice = async () => {
    try {
      const formData = new FormData();
      for (const key in newInvoice) {
        formData.append(key, newInvoice[key]);
      }
      const response = await axios.post('http://localhost:5001/api/invoices', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      if (response.data && response.data.success) {
        setInvoices([...invoices, response.data.invoice]);
        setNewInvoice({ invoiceNumber: '', date: '', time: '', receiptType: '', narrative: '', amount: '', currency: '', image: null });
        setShowPopup(false);
      }
    } catch (error) {
      console.error('Failed to add invoice', error);
    }
  };

  const handleEditInvoice = (invoice) => {
    setEditInvoice(invoice);
    setPopupMode('edit');
    setShowPopup(true);
  };

  const handleUpdateInvoice = async () => {
    try {
      const formData = new FormData();
      for (const key in editInvoice) {
        formData.append(key, editInvoice[key]);
      }
      const response = await axios.put(`http://localhost:5001/api/invoices/${editInvoice.id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      if (response.data && response.data.success) {
        setInvoices(invoices.map((inv) => (inv.id === editInvoice.id ? editInvoice : inv)));
        setEditInvoice(null);
        setShowPopup(false);
      }
    } catch (error) {
      console.error('Failed to update invoice', error);
    }
  };

  const handleDeleteInvoice = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this invoice?');
    if (confirmDelete) {
      try {
        const response = await axios.delete(`http://localhost:5001/api/invoices/${id}`);
        if (response.data && response.data.success) {
          setInvoices(invoices.filter((invoice) => invoice.id !== id));
        }
      } catch (error) {
        console.error('Failed to delete invoice', error);
      }
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (popupMode === 'add') {
      setNewInvoice({ ...newInvoice, image: file });
    } else {
      setEditInvoice({ ...editInvoice, image: file });
    }
  };

  return (
    <div className="invoice-container">
      <h2>Invoices</h2>
      <button className="add-invoice-button" onClick={() => { setPopupMode('add'); setShowPopup(true); }}>Add Invoice</button>

      {showPopup && (
        <div className="popup-overlay fancy-popup">
          <div className="popup fancy-popup-window">
            <h3>{popupMode === 'add' ? 'Add Invoice' : 'Edit Invoice'}</h3>
            <input
              type="text"
              placeholder="Invoice Number"
              value={popupMode === 'add' ? newInvoice.invoiceNumber : editInvoice.invoiceNumber}
              onChange={(e) => popupMode === 'add' ? setNewInvoice({ ...newInvoice, invoiceNumber: e.target.value }) : setEditInvoice({ ...editInvoice, invoiceNumber: e.target.value })}
            />
            <input
              type="date"
              placeholder="Date"
              value={popupMode === 'add' ? newInvoice.date : editInvoice.date}
              onChange={(e) => popupMode === 'add' ? setNewInvoice({ ...newInvoice, date: e.target.value }) : setEditInvoice({ ...editInvoice, date: e.target.value })}
            />
            <input
              type="time"
              placeholder="Time"
              value={popupMode === 'add' ? newInvoice.time : editInvoice.time}
              onChange={(e) => popupMode === 'add' ? setNewInvoice({ ...newInvoice, time: e.target.value }) : setEditInvoice({ ...editInvoice, time: e.target.value })}
            />
            <select
              value={popupMode === 'add' ? newInvoice.receiptType : editInvoice.receiptType}
              onChange={(e) => popupMode === 'add' ? setNewInvoice({ ...newInvoice, receiptType: e.target.value }) : setEditInvoice({ ...editInvoice, receiptType: e.target.value })}
            >
              <option value="">Select Receipt Type</option>
              <option value="Cash">Cash</option>
              <option value="Credit">Credit</option>
              <option value="Bank Transfer">Bank Transfer</option>
            </select>
            <textarea
              placeholder="Narrative"
              value={popupMode === 'add' ? newInvoice.narrative : editInvoice.narrative}
              onChange={(e) => popupMode === 'add' ? setNewInvoice({ ...newInvoice, narrative: e.target.value }) : setEditInvoice({ ...editInvoice, narrative: e.target.value })}
            />
            <input
              type="number"
              placeholder="Amount"
              value={popupMode === 'add' ? newInvoice.amount : editInvoice.amount}
              onChange={(e) => popupMode === 'add' ? setNewInvoice({ ...newInvoice, amount: e.target.value }) : setEditInvoice({ ...editInvoice, amount: e.target.value })}
            />
            <input
              type="text"
              placeholder="Currency"
              value={popupMode === 'add' ? newInvoice.currency : editInvoice.currency}
              onChange={(e) => popupMode === 'add' ? setNewInvoice({ ...newInvoice, currency: e.target.value }) : setEditInvoice({ ...editInvoice, currency: e.target.value })}
            />
            <input
              type="file"
              onChange={handleImageChange}
              accept="image/*"
            />
            <div className="popup-buttons">
              {popupMode === 'add' ? (
                <button onClick={handleAddInvoice} className="fancy-button">Add Invoice</button>
              ) : (
                <button onClick={handleUpdateInvoice} className="fancy-button">Update Invoice</button>
              )}
              <button onClick={() => setShowPopup(false)} className="fancy-button cancel-button">Cancel</button>
            </div>
          </div>
        </div>
      )}

      <table className="invoice-table fancy-table">
        <thead>
          <tr>
            <th>Invoice Number</th>
            <th>Date</th>
            <th>Time</th>
            <th>Receipt Type</th>
            <th>Narrative</th>
            <th>Amount</th>
            <th>Currency</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {invoices.map((invoice) => (
            <tr key={invoice.id} className="fancy-table-row">
              <td>{invoice.invoiceNumber}</td>
              <td>{invoice.date}</td>
              <td>{invoice.time}</td>
              <td>{invoice.receiptType}</td>
              <td>{invoice.narrative}</td>
              <td>{invoice.amount}</td>
              <td>{invoice.currency}</td>
              <td>
                {user?.role !== 'viewer' && (
                  <div className="action-buttons">
                    <button className="fancy-button edit-button" onClick={() => handleEditInvoice(invoice)}>Edit</button>
                    <button className="fancy-button delete-button" onClick={() => handleDeleteInvoice(invoice.id)}>Delete</button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InvoiceManagement;
