import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AppBar, Toolbar, Button } from '@mui/material';
import { UserContext } from '../context/UserContext';

const Navbar = () => {
  const { user, logout } = useContext(UserContext);

  console.log("Navbar User Context Value:", user); // Debug log to verify the user

  return (
    <AppBar position="static">
      <Toolbar>
        {user && (
          <>
            <Button color="inherit" component={Link} to="/invoices">Invoices</Button>
            {user?.role === 'admin' && <Button color="inherit" component={Link} to="/users">User Management</Button>}
            <Button color="inherit" onClick={logout}>Logout</Button>
          </>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
