import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../context/UserContext';
import './UserManagement.css';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const { user } = useContext(UserContext);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMode, setPopupMode] = useState(''); // 'add', 'edit', 'delete'
  const [selectedUser, setSelectedUser] = useState(null);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('user');
  const [password, setPassword] = useState('');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/users');
        if (response.data && Array.isArray(response.data.users)) {
          setUsers(response.data.users);
        } else {
          console.error('Unexpected response format', response.data);
          setUsers([]);
        }
      } catch (error) {
        console.error('Failed to fetch users', error);
      }
    };

    fetchUsers();
  }, []);

  const handleAddUser = () => {
    setPopupMode('add');
    setUsername('');
    setEmail('');
    setRole('user');
    setPassword('');
    setShowPopup(true);
  };

  const handleEditUser = (user) => {
    setSelectedUser(user);
    setPopupMode('edit');
    setUsername(user.username);
    setEmail(user.email);
    setRole(user.role);
    setPassword(''); // Password is required again if updating
    setShowPopup(true);
  };

  const handleSaveUser = async () => {
    try {
      if (popupMode === 'add') {
        // Create a new user
        const newUser = { username, email, role, password };
        const response = await axios.post('http://localhost:5001/api/users', newUser);
        if (response.data && response.data.id) {
          alert('User created successfully!');
          setUsers([...users, { ...newUser, id: response.data.id }]);
        }
      } else if (popupMode === 'edit') {
        // Update existing user
        const updatedUser = { username, email, role };
        if (password) {
          updatedUser.password = password; // Update password if provided
        }
        const response = await axios.put(`http://localhost:5001/api/users/${selectedUser.id}`, updatedUser);
        if (response.data && response.data.id) {
          alert('User updated successfully!');
          setUsers(users.map((u) => (u.id === selectedUser.id ? response.data : u)));
        }
      }
      setShowPopup(false);
    } catch (error) {
      console.error('Failed to save user', error);
      alert('An error occurred while saving the user.');
    }
  };

  const handleDeleteUser = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this user?');
    if (confirmDelete) {
      try {
        const response = await axios.delete(`http://localhost:5001/api/users/${id}`);
        if (response.data && response.data.message === 'User deleted successfully') {
          alert('User deleted successfully!');
          setUsers(users.filter((user) => user.id !== id));
        }
      } catch (error) {
        console.error('Failed to delete user', error);
      }
    }
  };

  return (
    <div className="user-container">
      <h2>User Management</h2>
      <button className="add-user-button fancy-button" onClick={handleAddUser}>Add User</button>

      {showPopup && (
        <div className="popup-overlay fancy-popup">
          <div className="popup fancy-popup-window">
            <h3>{popupMode === 'add' ? 'Add User' : 'Edit User'}</h3>
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="text"
              name="role"
              placeholder="Role (user/admin)"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <div className="popup-buttons">
              <button onClick={handleSaveUser} className="fancy-button">{popupMode === 'add' ? 'Add' : 'Save'}</button>
              <button onClick={() => setShowPopup(false)} className="fancy-button cancel-button">Cancel</button>
            </div>
          </div>
        </div>
      )}

      <table className="user-table fancy-table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id} className="fancy-table-row">
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <div className="action-buttons">
                  <button className="fancy-button edit-button" onClick={() => handleEditUser(user)}>Edit</button>
                  <button className="fancy-button delete-button" onClick={() => handleDeleteUser(user.id)}>Delete</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserManagement;
