import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../context/UserContext';
import InvoiceFormPopup from './InvoiceFormPopup';
import './InvoiceManagement.css';

const InvoiceManagement = () => {
  const [invoices, setInvoices] = useState([]);
  const { user } = useContext(UserContext);
  const [showPopup, setShowPopup] = useState(false);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [popupMode, setPopupMode] = useState(''); // 'add' or 'edit'
  const [selectedInvoice, setSelectedInvoice] = useState(null);
  const [receiptTypes] = useState(['Invoice', 'Gas', 'Support Office', 'Other']);

  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/invoices');
        if (response.data && Array.isArray(response.data.invoices)) {
          // Format date to dd-mm-yyyy for display purposes
          const formattedInvoices = response.data.invoices.map((invoice) => ({
            ...invoice,
            date: new Date(invoice.date).toLocaleDateString('en-GB'),
          }));
          setInvoices(formattedInvoices);
        } else {
          console.error('Unexpected response format', response.data);
          setInvoices([]);
        }
      } catch (error) {
        console.error('Failed to fetch invoices', error);
      }
    };

    fetchInvoices();
  }, []);

  const handleAddInvoice = async (invoice) => {
    try {
      const formData = new FormData();
      // Convert date to yyyy-mm-dd before submitting
      const invoiceToSubmit = {
        ...invoice,
        date: new Date(invoice.date.split('-').reverse().join('-')).toISOString().split('T')[0],
      };

      for (const key in invoiceToSubmit) {
        if (key === 'images' && invoiceToSubmit.images.length > 0) {
          invoiceToSubmit.images.forEach((image) => formData.append('images', image));
        } else {
          formData.append(key, invoiceToSubmit[key]);
        }
      }

      const response = await axios.post('http://localhost:5001/api/invoices', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      if (response.data && response.data.success) {
        setInvoices([...invoices, response.data.invoice]);
        setShowPopup(false);
        setShowSuccessPopup(true);
      }
    } catch (error) {
      console.error('Failed to add invoice', error);
    }
  };

  const handleEditInvoice = (invoice) => {
    setSelectedInvoice(invoice);
    setPopupMode('edit');
    setShowPopup(true);
  };

  const handleUpdateInvoice = async (updatedInvoice) => {
    try {
      const formData = new FormData();
      // Convert date to yyyy-mm-dd before submitting
      const invoiceToSubmit = {
        ...updatedInvoice,
        date: new Date(updatedInvoice.date.split('-').reverse().join('-')).toISOString().split('T')[0],
      };

      for (const key in invoiceToSubmit) {
        if (key === 'images' && invoiceToSubmit.images.length > 0) {
          invoiceToSubmit.images.forEach((image) => formData.append('images', image));
        } else {
          formData.append(key, invoiceToSubmit[key]);
        }
      }

      const response = await axios.put(`http://localhost:5001/api/invoices/${updatedInvoice.id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      if (response.data && response.data.success) {
        setInvoices(invoices.map((inv) => (inv.id === updatedInvoice.id ? response.data.invoice : inv)));
        setShowPopup(false);
        setShowSuccessPopup(true);
      }
    } catch (error) {
      console.error('Failed to update invoice', error);
    }
  };

  const handleDeleteInvoice = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this invoice?');
    if (confirmDelete) {
      try {
        const response = await axios.delete(`http://localhost:5001/api/invoices/${id}`);
        if (response.data && response.data.success) {
          setInvoices(invoices.filter((invoice) => invoice.id !== id));
        }
      } catch (error) {
        console.error('Failed to delete invoice', error);
      }
    }
  };

  return (
    <div className="invoice-container">
      <h2>Invoices</h2>
      <button className="add-invoice-button fancy-button" onClick={() => { setPopupMode('add'); setShowPopup(true); }}>Add Invoice</button>

      {showPopup && (
        <InvoiceFormPopup
          invoice={popupMode === 'edit' ? selectedInvoice : null}
          onSave={popupMode === 'add' ? handleAddInvoice : handleUpdateInvoice}
          onClose={() => setShowPopup(false)}
          receiptTypes={receiptTypes}
        />
      )}

      {showSuccessPopup && (
        <div className="success-popup">
          <div className="success-popup-content">
            <p>{popupMode === 'add' ? 'Invoice added successfully!' : 'Invoice updated successfully!'}</p>
            <button onClick={() => setShowSuccessPopup(false)} className="fancy-button">Close</button>
          </div>
        </div>
      )}

      <table className="invoice-table fancy-table">
        <thead>
          <tr>
            <th>Invoice Number</th>
            <th>Receipt Number</th>
            <th>Date</th>
            <th>Time</th>
            <th>Receipt Type</th>
            <th>Narrative</th>
            <th>Amount</th>
            <th>Currency</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {invoices.map((invoice) => (
            <tr key={invoice.id} className="fancy-table-row">
              <td>{invoice.invoiceNumber}</td>
              <td>{invoice.receiptNumber}</td>
              <td>{invoice.date}</td>
              <td>{invoice.time}</td>
              <td>{invoice.receiptType}</td>
              <td>{invoice.narrative}</td>
              <td>{invoice.amount}</td>
              <td>{invoice.currency}</td>
              <td>
                {user?.role !== 'viewer' && (
                  <div className="action-buttons">
                    <button className="fancy-button edit-button" onClick={() => handleEditInvoice(invoice)}>Edit</button>
                    <button className="fancy-button delete-button" onClick={() => handleDeleteInvoice(invoice.id)}>Delete</button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InvoiceManagement;
