import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../context/UserContext';
import './InvoiceManagement.css';

const InvoiceManagement = () => {
  const [invoices, setInvoices] = useState([]);
  const { user } = useContext(UserContext);
  const [newInvoice, setNewInvoice] = useState({ receiptNumber: '', invoiceNumber: '', amount: '' });
  const [editInvoice, setEditInvoice] = useState(null);

  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/invoices');
        if (response.data && Array.isArray(response.data.invoices)) {
          setInvoices(response.data.invoices);
        } else {
          console.error('Unexpected response format', response.data);
          setInvoices([]);
        }
      } catch (error) {
        console.error('Failed to fetch invoices', error);
      }
    };
    fetchInvoices();
  }, []);

  const handleAddInvoice = async () => {
    try {
      const response = await axios.post('http://localhost:5001/api/invoices', newInvoice);
      if (response.data && response.data.success) {
        setInvoices([...invoices, response.data.invoice]);
        setNewInvoice({ receiptNumber: '', invoiceNumber: '', amount: '' });
      }
    } catch (error) {
      console.error('Failed to add invoice', error);
    }
  };

  const handleEditInvoice = (invoice) => {
    setEditInvoice(invoice);
  };

  const handleUpdateInvoice = async () => {
    try {
      const response = await axios.put(`http://localhost:5001/api/invoices/${editInvoice.id}`, editInvoice);
      if (response.data && response.data.success) {
        setInvoices(invoices.map((inv) => (inv.id === editInvoice.id ? editInvoice : inv)));
        setEditInvoice(null);
      }
    } catch (error) {
      console.error('Failed to update invoice', error);
    }
  };

  const handleDeleteInvoice = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this invoice?');
    if (confirmDelete) {
      try {
        const response = await axios.delete(`http://localhost:5001/api/invoices/${id}`);
        if (response.data && response.data.success) {
          setInvoices(invoices.filter((invoice) => invoice.id !== id));
        }
      } catch (error) {
        console.error('Failed to delete invoice', error);
      }
    }
  };

  return (
    <div className="invoice-container">
      <h2>Invoices</h2>
      <div className="add-invoice-form">
        <input
          type="text"
          placeholder="Receipt Number"
          value={newInvoice.receiptNumber}
          onChange={(e) => setNewInvoice({ ...newInvoice, receiptNumber: e.target.value })}
        />
        <input
          type="text"
          placeholder="Invoice Number"
          value={newInvoice.invoiceNumber}
          onChange={(e) => setNewInvoice({ ...newInvoice, invoiceNumber: e.target.value })}
        />
        <input
          type="number"
          placeholder="Amount"
          value={newInvoice.amount}
          onChange={(e) => setNewInvoice({ ...newInvoice, amount: e.target.value })}
        />
        <button onClick={handleAddInvoice}>Add Invoice</button>
      </div>
      {editInvoice && (
        <div className="edit-invoice-form">
          <h3>Edit Invoice</h3>
          <input
            type="text"
            placeholder="Receipt Number"
            value={editInvoice.receiptNumber}
            onChange={(e) => setEditInvoice({ ...editInvoice, receiptNumber: e.target.value })}
          />
          <input
            type="text"
            placeholder="Invoice Number"
            value={editInvoice.invoiceNumber}
            onChange={(e) => setEditInvoice({ ...editInvoice, invoiceNumber: e.target.value })}
          />
          <input
            type="number"
            placeholder="Amount"
            value={editInvoice.amount}
            onChange={(e) => setEditInvoice({ ...editInvoice, amount: e.target.value })}
          />
          <button onClick={handleUpdateInvoice}>Update Invoice</button>
        </div>
      )}
      <table className="invoice-table">
        <thead>
          <tr>
            <th>Receipt Number</th>
            <th>Invoice Number</th>
            <th>Amount</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {invoices.map((invoice) => (
            <tr key={invoice.id}>
              <td>{invoice.receiptNumber}</td>
              <td>{invoice.invoiceNumber}</td>
              <td>{invoice.amount}</td>
              <td>
                {user?.role !== 'viewer' && (
                  <div className="action-buttons">
                    <button className="edit-button" onClick={() => handleEditInvoice(invoice)}>Edit</button>
                    <button className="delete-button" onClick={() => handleDeleteInvoice(invoice.id)}>Delete</button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InvoiceManagement;
