// src/App.js
import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import UserManagement from './components/UserManagement';
import Navbar from './components/Navbar';
import { UserContext } from './context/UserContext';

const ProtectedRoute = ({ children, roles }) => {
  const { token, userRole } = useContext(UserContext);

  if (!token) {
    return <Navigate to="/login" />;
  }
  if (roles.includes(userRole)) {
    return children;
  }
  return <Navigate to="/" />;
};

const App = () => {
  const { token, setToken, userRole } = useContext(UserContext);

  return (
    <Router>
      <Navbar token={token} userRole={userRole} setToken={setToken} />
      <Routes>
        <Route path="/login" element={<Login setToken={setToken} />} />
        <Route
          path="/"
          element={
            <ProtectedRoute roles={['admin', 'editor', 'viewer']}>
              <InvoiceList token={token} userRole={userRole} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/add-invoice"
          element={
            <ProtectedRoute roles={['admin', 'editor']}>
              <InvoiceForm token={token} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/edit-invoice/:id"
          element={
            <ProtectedRoute roles={['admin', 'editor']}>
              <InvoiceForm token={token} />
            </ProtectedRoute>
          }
        />
        <Route
          path="/view-invoice/:id"
          element={
            <ProtectedRoute roles={['admin', 'editor', 'viewer']}>
              <InvoiceDetail />
            </ProtectedRoute>
          }
        />
        <Route
          path="/user-management"
          element={
            <ProtectedRoute roles={['admin']}>
              <UserManagement token={token} />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
